import React, { useState, useEffect } from "react";
import "../../App.css";
import { Modal, Button } from "react-bootstrap";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import UpdateConfirmationalModal from "./confirmationalmodal/UpdateConfirmationalModal";
import DeleteConfirmationModal from "./confirmationalmodal/DeleteConfirmationalModal";

function EditUserModal(props) {
    const { show, onHide, userDetails } = props;
    const [id, setId] = useState("");
    const [name, setName] = useState("");
    const [address, setAddress] = useState("");
    const [type, setType] = useState("");
    const [status, setStatus] = useState("");
    const [price, setPrice] = useState("");
    const [size, setSize] = useState("");
    const [description, setDescription] = useState("");
    const [showConfirmModal, setShowConfirmModal] = useState(false);
    const [showUpdateConfirmModal, setShowUpdateConfirmModal] = useState(false);
    const [updateTrigger, setUpdateTrigger] = useState(false);


    useEffect(() => {
        if (userDetails) {
            setName(userDetails.name);
            setAddress(userDetails.address);
            setType(userDetails.type);
            setStatus(userDetails.status);
            setPrice(userDetails.price);
            setSize(userDetails.size);
            setDescription(userDetails.description);
            setId(userDetails.id)

        }
    }, [userDetails]);

    const handleUpdate = (e) => {
        setShowUpdateConfirmModal(true);
    };

    async function handleUpdateConfirmed(e) {
        e.preventDefault();
        if (
            name === userDetails.name &&
            address === userDetails.address &&
            type === userDetails.type &&
            status === userDetails.status &&
            price === userDetails.price &&
            size === userDetails.size &&
            description === userDetails.description
        ) {
            toast.info("No data to update");
            setShowUpdateConfirmModal(false);
            return;
        }
        try {
            const userProperty = {
                id: id,
                name: name,
                address: address,
                type: type,
                status: status,
                price: price,
                size: size,
                description: description,
            };
            await axios
                .put(`http://127.0.0.1:8000/user/${id}`, updateduser)
                .then((response) => {
                    toast.success("user Updated successfully");
                    onHide();
                    setUpdateTrigger(!updateTrigger);
                });
        } catch (error) {
            alert("user Update Failed");
            console.error(error);
        }
        setShowUpdateConfirmModal(false);
    }
    const handleDelete = (e) => {
        setShowConfirmModal(true);
    };

    async function handleConfirmed(e) {
        e.preventDefault();
        try {
            await axios
                .delete(`http://127.0.0.1:8000/user/${id}`)
                .then((response) => {
                    toast.success("user deleted successfully");
                    onHide();
                });
        } catch (error) {
            alert("user Delete Failed");
            console.error(error);
        }
        setShowConfirmModal(false);
    }



    return (

        <Modal show={show} onHide={onHide} centered backdrop="static" size="lg">
            <Modal.Header closeButton>
                <Modal.Title className="Modal-Title">user Details Edit</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <>
                    <div className="d-flex row">
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    User Name
                                </label>
                                <input
                                    type="text"
                                    class="form-control"
                                    id="exampleFormControlInput1"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}

                                />
                            </div>
                        </div>
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    Address
                                </label>
                                <input
                                    type="text"
                                    class="form-control"
                                    id="exampleFormControlInput1"
                                    value={address}
                                    onChange={(e) => setAddress(e.target.value)}

                                />
                            </div>
                        </div>
                    </div>
                    <div className="d-flex row">
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    Type
                                </label>
                                <select
                                    id="selectAddAttribute-attribute"
                                    className="form-select"
                                    onChange={(e) => setType(e.target.value)}
                                    value={type}
                                >
                                    <option value={""}>--Select an Type--</option>
                                    <option value={"House"}>House</option>
                                    <option value={"Yard"}>Yard</option>
                                    <option value={"Apartments"}>Apartments</option>
                                    <option value={"Yard"}>Hotel</option>
                                    <option value={"Warehouse"}>Warehouse</option>
                                </select>
                            </div>
                        </div>
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    Status
                                </label>
                                <select
                                    id="selectAddAttribute-attribute"
                                    className="form-select"
                                    value={status}
                                    onChange={(e) => setStatus(e.target.value)}

                                >
                                    <option value={""}>--Select an Status--</option>
                                    <option value={"Active"}>Active</option>
                                    <option value={"Pending"}>Pending</option>
                                    <option value={"Sold"}>Sold</option>
                                </select>
                                {/* <input
                                    type="text"
                                    class="form-control"
                                    id="exampleFormControlInput1"
                                    onChange={(e) => setStatus(e.target.value)}
                                    value={status}
                                /> */}
                            </div>
                        </div>
                    </div>
                    <div className="d-flex row">
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    Price
                                </label>
                                <input
                                    type="text"
                                    class="form-control"
                                    id="exampleFormControlInput1"
                                    onChange={(e) => setPrice(e.target.value)}
                                    value={price}
                                />
                            </div>
                        </div>
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    Size
                                </label>
                                <input
                                    type="text"
                                    class="form-control"
                                    id="exampleFormControlInput1"
                                    onChange={(e) => setSize(e.target.value)}
                                    value={size}
                                />
                            </div>
                        </div>
                    </div>
                    <div className="d-flex row">
                        <div className="col-12">
                            <div className="form-outline mb-2">
                                <label className="form-label" htmlFor="inputAddAttribute-value">
                                    User Description
                                </label>
                                <textarea
                                    type="text"
                                    id="inputAddAttribute-value"
                                    className="form-control"
                                    onChange={(e) => setDescription(e.target.value)}
                                    value={description}
                                />
                            </div>
                        </div>
                    </div>
                </>
            </Modal.Body>

            <Modal.Footer>
                <Button variant="primary" style={{ backgroundColor: 'green' }} onClick={handleUpdate}>
                    Update User
                </Button>
                <Button variant="danger" onClick={handleDelete}>
                    Delete
                </Button>
                <ToastContainer />
                <UpdateConfirmationalModal
                    show={showUpdateConfirmModal}
                    onHide={() => setShowUpdateConfirmModal(false)}
                    onConfirm={handleUpdateConfirmed}
                />
                <DeleteConfirmationModal
                    show={showConfirmModal}
                    onHide={() => setShowConfirmModal(false)}
                    onConfirm={handleConfirmed}
                />
            </Modal.Footer>
        </Modal>
    );
}

export default EditUserModal;
