import React, { useState, useEffect } from "react";
import "../../App.css";
import { Modal, Button } from "react-bootstrap";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function EditPropertyModal(props) {
    const { show, onHide, propertyDetails } = props;
    const [name, setName] = useState("");
    const [address, setAddress] = useState("");
    const [type, setType] = useState("");
    const [status, setStatus] = useState("");
    const [price, setPrice] = useState("");
    const [size, setSize] = useState("");
    const [description, setDescription] = useState("");
    const [showConfirmModal, setShowConfirmModal] = useState(false);
    const [showUpdateConfirmModal, setShowUpdateConfirmModal] = useState(false);
    const [updateTrigger, setUpdateTrigger] = useState(false);


    useEffect(() => {
        if (propertyDetails) {
            setName(propertyDetails.name);
            setAddress(propertyDetails.address);
            setType(propertyDetails.type);
            setStatus(propertyDetails.status);
            setPrice(propertyDetails.price);
            setSize(propertyDetails.size);
            setDescription(propertyDetails.description);

        }
    }, [propertyDetails]);

    const handleUpdate = (e) => {
        setShowUpdateConfirmModal(true);
      };

      async function handleUpdateConfirmed(e) {
        e.preventDefault();
        if (
          categories === categoryDetails.categories &&
          status === categoryDetails.status
        ) {
          toast.info("No data to update");
          setShowUpdateConfirmModal(false);
          return;
        }
        try {
          const updatedCategory = {
            id: id,
            categories: categories,
            status: status,
          };
          await axios
            .put(`http://127.0.0.1:8000/category/${id}`, updatedCategory)
            .then((response) => {
              toast.success("Category Updated successfully");
              onHide();
              setUpdateTrigger(!updateTrigger);
            });
        } catch (error) {
          alert("Category Update Failed");
          console.error(error);
        }
        setShowUpdateConfirmModal(false);
      }
      const handleDelete = (e) => {
        setShowConfirmModal(true);
      };

      async function handleConfirmed(e) {
        e.preventDefault();
        try {
          await axios
            .delete(`http://127.0.0.1:8000/category/${id}`)
            .then((response) => {
              toast.success("Category deleted successfully");
              onHide();
            });
        } catch (error) {
          alert("Category Delete Failed");
          console.error(error);
        }
        setShowConfirmModal(false);
      }



    return (

        <Modal show={show} onHide={onHide} centered backdrop="static" size="lg">
            <Modal.Header closeButton>
                <Modal.Title className="Modal-Title">Property Details Edit</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <>
                    <div className="d-flex row">
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    Property Name
                                </label>
                                <input
                                    type="text"
                                    class="form-control"
                                    id="exampleFormControlInput1"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}

                                />
                            </div>
                        </div>
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    Address
                                </label>
                                <input
                                    type="text"
                                    class="form-control"
                                    id="exampleFormControlInput1"
                                    value={address}
                                    onChange={(e) => setAddress(e.target.value)}

                                />
                            </div>
                        </div>
                    </div>
                    <div className="d-flex row">
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    Type
                                </label>
                                <input
                                    type="text"
                                    class="form-control"
                                    id="exampleFormControlInput1"
                                    value={type}
                                    onChange={(e) => setType(e.target.value)}

                                />
                            </div>
                        </div>
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    Status
                                </label>
                                <select
                                    id="selectAddAttribute-attribute"
                                    className="form-select"
                                    value={status}
                                    onChange={(e) => setStatus(e.target.value)}

                                >
                                    <option value={""}>--Select an Attribute--</option>
                                    <option value={"Available"}>Available</option>
                                    <option value={"Non-Available"}>NonAvailable</option>
                                </select>
                                {/* <input
                                    type="text"
                                    class="form-control"
                                    id="exampleFormControlInput1"
                                    onChange={(e) => setStatus(e.target.value)}
                                    value={status}
                                /> */}
                            </div>
                        </div>
                    </div>
                    <div className="d-flex row">
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    Price
                                </label>
                                <input
                                    type="text"
                                    class="form-control"
                                    id="exampleFormControlInput1"
                                    onChange={(e) => setPrice(e.target.value)}
                                    value={price}
                                />
                            </div>
                        </div>
                        <div className="col-6">
                            <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">
                                    Size
                                </label>
                                <input
                                    type="text"
                                    class="form-control"
                                    id="exampleFormControlInput1"
                                    onChange={(e) => setSize(e.target.value)}
                                    value={size}
                                />
                            </div>
                        </div>
                    </div>
                    <div className="d-flex row">
                        <div className="col-12">
                            <div className="form-outline mb-2">
                                <label className="form-label" htmlFor="inputAddAttribute-value">
                                    Property Description
                                </label>
                                <textarea
                                    type="text"
                                    id="inputAddAttribute-value"
                                    className="form-control"
                                    onChange={(e) => setDescription(e.target.value)}
                                    value={description}
                                />
                            </div>
                        </div>
                    </div>
                </>
            </Modal.Body>

            <Modal.Footer>
                <Button variant="success" onClick={addProperty}>
                    Update Property
                </Button>
                <Button variant="danger" onClick={resetForm}>
                    delete
                </Button>
                <ToastContainer />
                <UpdateConfirmation
            show={showUpdateConfirmModal}
            onHide={() => setShowUpdateConfirmModal(false)}
            onConfirm={handleUpdateConfirmed}
          />
            </Modal.Footer>
        </Modal>
    );
}

export default EditPropertyModal;
